// we will make two config, one for devs and one for production


const config = {};

// for devs
config.staging = {
	name:'staging',
	port:3000,
}

// for production
config.production = {
	name:'production',
	port:5000,

}


// then we look at the NODE_ENV to see which one is selected.
// if none or unknown, we default to staging

const env = typeof(process.env.NODE_ENV) === 'string' ? process.env.NODE_ENV : '';
const chosenConfig = config[env] ? config[env] : config.staging;


module.exports = chosenConfig;
