// join all the handlers

// dependencies
const userHandler = require('handlers/userHandler').handler;
const tokenHandler = require('handlers/tokenHandler').handler;
const errors = require('errors');


const handlers = {
	users:userHandler,
	tokens:tokenHandler,
};

// console.log(errors.notFound('route').getClient());

handlers.ping = data => Promise.resolve();
handlers.notFound = data => Promise.reject(errors.notFound('route'));

module.exports = handlers;
